package tk.dao;import java.util.ArrayList;import java.util.HashMap;import java.util.Iterator;import java.util.Map;import tk.db.DBUnits;public class QUser {	public String getPassword(String strname) {		DBUnits units = new DBUnits();				String strsql = "select * from users where name = '"+strname +"'";		String strpd = null;				ArrayList list= units.getQuery1(strsql);		if (list.size()!=0){		Iterator it = list.iterator();		Map map = new HashMap();		map = (Map) it.next();				for(int i = 0; i < list.size();i++){			strpd = (String) map.get("password"); 			}		}		return strpd;	}			public static void main(String[] args) {		QUser quser = new QUser();		System.out.println(quser.getPassword(""));	}}